import axios from "axios";

const fakeStoreApi = axios.create({
  baseURL: "https://fakestoreapi.com/products?limit=5",
});

const customApi = axios.create({
  baseURL: "https://webapp353621.ip-45-79-142-173.cloudezapp.io/api",
});

const getFakeStoreProducts = async () => {
  try {
    const response = await fakeStoreApi.get();
    return response.data;
  } catch (error) {
    console.error("Error fetching products from Fake Store API:", error);
    return [];
  }
};

const getCustomApiItems = async () => {
  try {
    const response = await customApi.get();
    return response.data;
  } catch (error) {
    console.error("Error fetching items from Custom API:", error);
    return [];
  }
};

export { getFakeStoreProducts, getCustomApiItems };
