import { useState } from "react";
import { useEffect } from "react";
function ConponeteNome() {
  const [nome, setNome] = useState("SEU NOME");
  useEffect(() => {
    alert("O nome foi alterado");
  }, [nome]);

  return (
    <div>
      <button onClick={() => setNome("SEU NOME")}>Altera para apelido</button>
      <br />
      <button onClick={() => setNome("SEU NOME APELIDO")}>
        Altera para seu nome
      </button>
      <button onClick={() => setNome("Silvio Cruz")}>
        Altera para Silvio Cruz
      </button>
      <p>Nome: {nome}</p>
    </div>
  );
}
export { ConponeteNome };

