import { useContext } from "react";
import { Button, Card } from "react-bootstrap";
import { CarrinhoContext } from "../hooks/CarrinhoContext";
import { useNavigate } from "react-router-dom";

function CardProduto({ idProduto, nome, preco, descricao, linkImagem }) {
  const { adicionarCarrinho } = useContext(CarrinhoContext);
  let navigate = useNavigate();

  function adicionarNoCarrinho(
    idProduto,
    nomeProduto,
    precoProduto,
    descricaoProduto,
    linkImagemProduto
  ) {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login");
    }

    adicionarCarrinho({
      idProduto,
      nomeProduto,
      descricaoProduto,
      precoProduto,
      linkImagemProduto,
    });
  }

  return (
    <Card style={styles.card}>
      <Card.Img variant="top" src={linkImagem} style={styles.cardImg} />
      <Card.Body style={styles.cardBody}>
        <Card.Title style={styles.cardTitle}>
          {nome} - R$: {preco}
        </Card.Title>
        <Card.Text style={styles.cardText}>{descricao}</Card.Text>
        <Button
          variant="primary"
          onClick={() =>
            adicionarNoCarrinho(idProduto, nome, preco, descricao, linkImagem)
          }
          style={styles.button}
        >
          ADICIONAR
        </Button>
      </Card.Body>
    </Card>
  );
}

const styles = {
  card: {
    width: "18rem",
    margin: "1rem",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
    borderRadius: "10px",
    overflow: "hidden",
    transition: "transform 0.2s",
  },
  cardImg: {
    height: "200px",
    objectFit: "cover",
  },
  cardBody: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  cardTitle: {
    fontSize: "1.25rem",
    fontWeight: "bold",
    textAlign: "center",
  },
  cardText: {
    textAlign: "center",
    marginBottom: "1rem",
  },
  button: {
    backgroundColor: "#007bff",
    borderColor: "#007bff",
    borderRadius: "50px",
    padding: "0.5rem 1.5rem",
    transition: "background-color 0.2s, transform 0.2s",
  },
};

export { CardProduto };
